function addHyphen(b){const a=document.getElementById("jform_code");let code=a.value,hyphenDeleted;code=code.replace(/[^0-9a-f]/g,"");hyphenDeleted=b.inputType==="deleteContentBackward"&&a.value.length===4&&code.length===4;if(hyphenDeleted){code=code.substr(0,3)}if(code.length>=4){code=code.substr(0,4)+"-"+code.substr(4,4)}a.value=code}window.onload=function(){const a=document.getElementById("jform_code");if(a!==null){a.addEventListener("input",addHyphen)}};